    src: local('Charter Regular'),          local('Charter-Regular'),
         url('/fonts/charter_regular.woff') format('woff'),     /* Modern Browsers */
         url('/fonts/charter_regular.ttf')  format('truetype'); /* Safari, Android, iOS */
}

