#!/bin/sh

# make temporary profile directory
PROFILE_DIR="$(mktemp -d -p /tmp 'firefox.XXXXXX')"

cleanup() {
    rm -rvf "$PROFILE_DIR"
}

firefox-esr --new-instance --no-remote --profile "$PROFILE_DIR" "$@"

cleanup

#!/bin/sh
# trap everything; clean up on exit
trap cleanup 1 2 3 6 15

# make temporary profile directory
PROFILE_DIR="$(mktemp -d -p /tmp 'firefox.XXXXXX')"

cleanup() {
    rm -rvf "$PROFILE_DIR"
}

firefox-esr --new-instance --no-remote --profile "$PROFILE_DIR" "$@"

cleanup

#!/bin/sh
# trap everything; clean up on exit
trap cleanup 1 2 3 6 15

# make temporary profile directory
PROFILE_DIR="$(mktemp -d -p /tmp 'chrome.XXXXXX')"

cleanup() {
    rm -rvf "$PROFILE_DIR"
}

chromium --user-data-dir="$PROFILE_DIR" "$@"

cleanup

#!/bin/sh # trap everything; clean up on exit trap cleanup 1 2 3 6 15
# setup fake home dir FAKEHOME="$(mktemp -d -p /tmp 'firefox.XXXXXX')"
# clean up after exit cleanup() { rm -rvf "$FAKEHOME" }
# link Xauthority ln -s "${HOME}/.Xauthority" "${FAKEHOME}/.Xauthority"
# unset XDG dirs as these can intefere with the fake $HOME if they're set # to something non-default unset XDG_CONFIG_HOME unset XDG_CACHE_HOME
# set the fake home dir export HOME=${FAKEHOME}
# run firefox w/ given args firefox-esr --new-instance --no-remote "$@"
# uncomment for chrome # chromium "$@"
cleanup

